import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

// Données de test
const sampleQuizData = {
  questions: [
    {
      id: 1,
      question: "Quelle est la définition de l'ISA (International Standard Atmosphere) au niveau de la mer ?",
      options: [
        "15°C, 1013.25 hPa, 1.225 kg/m³",
        "0°C, 1013.25 hPa, 1.225 kg/m³",
        "15°C, 1023.25 hPa, 1.225 kg/m³",
        "15°C, 1013.25 hPa, 1.325 kg/m³"
      ],
      correctAnswer: 0
    },
    {
      id: 2,
      question: "À quelle altitude la pression atmosphérique est-elle réduite de moitié par rapport au niveau de la mer ?",
      options: [
        "18,000 ft",
        "5,500 m",
        "7,000 m",
        "22,000 ft"
      ],
      correctAnswer: 1
    }
  ]
};

export default function QuizPage() {
  const { subject } = useParams();
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [score, setScore] = useState(0);
  const [showResults, setShowResults] = useState(false);

  const handleAnswerSelect = (answerIndex) => {
    setSelectedAnswer(answerIndex);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer === sampleQuizData.questions[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }

    if (currentQuestion < sampleQuizData.questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
    } else {
      setShowResults(true);
    }
  };

  if (showResults) {
    return (
      <div className="container mx-auto px-4">
        <div className="bg-white rounded-lg shadow p-6 max-w-2xl mx-auto">
          <h2 className="text-2xl font-bold mb-4">Quiz terminé !</h2>
          <p className="text-lg mb-4">Votre score : {score} sur {sampleQuizData.questions.length}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Recommencer
          </button>
        </div>
      </div>
    );
  }

  const currentQuestionData = sampleQuizData.questions[currentQuestion];

  return (
    <div className="container mx-auto px-4">
      <div className="bg-white rounded-lg shadow p-6 max-w-2xl mx-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Quiz : {subject}</h2>
          <span className="text-gray-600">
            Question {currentQuestion + 1}/{sampleQuizData.questions.length}
          </span>
        </div>

        <div className="mb-6">
          <h3 className="text-lg mb-4">{currentQuestionData.question}</h3>
          <div className="space-y-2">
            {currentQuestionData.options.map((option, index) => (
              <div 
                key={index}
                className={`p-3 border rounded cursor-pointer ${
                  selectedAnswer === index ? 'bg-blue-100 border-blue-500' : 'hover:bg-gray-50'
                }`}
                onClick={() => handleAnswerSelect(index)}
              >
                {option}
              </div>
            ))}
          </div>
        </div>

        <button 
          onClick={handleNextQuestion}
          disabled={selectedAnswer === null}
          className={`w-full py-2 px-4 rounded ${
            selectedAnswer === null
              ? 'bg-gray-300 cursor-not-allowed'
              : 'bg-blue-500 text-white hover:bg-blue-600'
          }`}
        >
          {currentQuestion === sampleQuizData.questions.length - 1 ? 'Terminer' : 'Question suivante'}
        </button>
      </div>
    </div>
  );
}
