import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function QuizResults({ questions, answers, score, subject }) {
  const navigate = useNavigate();

  const getAnswerStatus = (question, userAnswerIndex) => {
    const isCorrect = userAnswerIndex === question.correctAnswer;
    return {
      isCorrect,
      userAnswer: question.options[userAnswerIndex],
      correctAnswer: question.options[question.correctAnswer]
    };
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow-lg p-6 max-w-3xl mx-auto">
        <h2 className="text-2xl font-bold mb-6">Résultats du Quiz : {subject}</h2>

        <div className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <div className="text-lg">
              Score final : <span className="font-bold">{score}</span> sur {questions.length}
            </div>
            <div className="text-lg">
              Pourcentage : <span className="font-bold">{Math.round((score / questions.length) * 100)}%</span>
            </div>
          </div>

          <div className="w-full bg-gray-200 rounded-full h-4">
            <div 
              className="bg-blue-600 h-4 rounded-full transition-all duration-500" 
              style={{ width: `${(score / questions.length) * 100}%` }}
            />
          </div>
        </div>

        <div className="space-y-6">
          {questions.map((question, index) => {
            const answerStatus = getAnswerStatus(question, answers[question.id]);

            return (
              <div key={question.id} className="border rounded-lg p-4">
                <div className="flex items-center mb-2">
                  <span className="text-gray-600 mr-2">Question {index + 1}</span>
                  {answerStatus.isCorrect ? (
                    <span className="bg-green-100 text-green-800 text-sm font-medium px-2.5 py-0.5 rounded">Correct</span>
                  ) : (
                    <span className="bg-red-100 text-red-800 text-sm font-medium px-2.5 py-0.5 rounded">Incorrect</span>
                  )}
                </div>

                <p className="font-medium mb-2">{question.question}</p>

                <div className="ml-4 space-y-1">
                  <p>
                    <span className="text-gray-600">Votre réponse : </span>
                    <span className={answerStatus.isCorrect ? 'text-green-600' : 'text-red-600'}>
                      {answerStatus.userAnswer}
                    </span>
                  </p>
                  {!answerStatus.isCorrect && (
                    <p className="text-green-600">
                      <span className="text-gray-600">Réponse correcte : </span>
                      {answerStatus.correctAnswer}
                    </p>
                  )}
                </div>

                <p className="mt-2 text-sm text-gray-600">{question.explanation}</p>
              </div>
            );
          })}
        </div>

        <div className="mt-8 flex space-x-4">
          <button 
            onClick={() => navigate('/')}
            className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
          >
            Retour à l'accueil
          </button>
          <button 
            onClick={() => window.location.reload()}
            className="bg-green-500 text-white px-6 py-2 rounded-lg hover:bg-green-600 transition-colors"
          >
            Recommencer le quiz
          </button>
        </div>
      </div>
    </div>
  );
}
