import React from "react";
import UpcomingEvents from '../components/quiz/UpcomingEvents';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import ProgressLeaning from '../components/quiz/ProgressLearning';
import search_icon from '../components/images/search_icon.png';
import notifications_icon from '../components/images/notifications_icon.png';
import account_circle_icon from '../components/images/account_circle_icon.png';
import SidebarLeft from "../components/common/SideBarLeft";

// Données statiques pour les graphiques
const gradeData = [
  { name: 'Phase 1', score: 6 },
  { name: 'Phase 2', score: 8 },
  { name: 'Phase 3', score: 9 },
  { name: 'Phase 4', score: 7 },
  { name: 'Phase 5', score: 10 },
  { name: 'Phase 6', score: 7 },
  { name: 'Phase 7', score: 8 },
];

const learningPathData = [
  { name: 'Completed', value: 70 },
  { name: 'Remaining', value: 30 },
];

// Couleurs pour le diagramme circulaire
const COLORS = ['#0088FE', '#FFBB28'];

const HomePage = () => {
  return (
    <div className="flex flex-col lg:flex-row p-6 gap-6">
      {/* Colonne gauche - Contenu principal */}
      <div className="flex-1 lg:mr-6"> {/* Ajout de marge à droite pour la colonne fixe */}
        {/* Contenu de la colonne gauche ici... */}

        {/* Graphiques */}
        <div className="grid grid-cols-2 gap-4">
          {/* Graphique des notes */}
          <div className="bg-white shadow-md rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Grade for Phase 2</h2>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={gradeData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="score" fill="#4169e1" />
              </BarChart>
            </ResponsiveContainer>
          </div>

          {/* Learning Path */}
          <div className="bg-white shadow-md rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Learning Path</h2>
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie
                  data={learningPathData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {learningPathData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      <SidebarLeft />
    </div>
  );
};

export default HomePage;
